import React, { useState, useEffect } from "react";
import { FaCamera, FaVideo, FaLine, FaCalendar } from "react-icons/fa";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export function CreatePost() {
  const [postType, setPostType] = useState("text");
  const [postText, setPostText] = useState("");
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState(null);
  const [eventDate, setEventDate] = useState(new Date());
  const [modalOpen, setModalOpen] = useState(false);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(parseInt(storedUserId));
    }
  }, []);

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setMediaFile(file);
      setMediaPreview(URL.createObjectURL(file));
    }
  };

  const handlePostSubmit = async (event) => {
    event.preventDefault();

    let postData = {
      userId: userId,
      type: postType,
      text: postText,
      date: new Date()
    };

    if (postType === 'evento') {
      postData.date = eventDate;
    }

    if (postType === 'meet') {
      postData.title = document.getElementById('meetTitle').value;
      postData.linkSala = document.getElementById('meetLink').value; 
    }

    if (postType === 'startup') {
      postData.title = document.getElementById('startupName').value;
    }

    if (postType === 'evento') {
      postData.title = document.getElementById('eventName').value;
      postData.local = document.getElementById('eventLocation').value;
    }

    const formData = new FormData();
    formData.append('data', JSON.stringify(postData)); 
    formData.append('mediaFile', mediaFile); 

    try {
      const response = await fetch('http://localhost:3001/CreatePost', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        throw new Error('Erro ao criar postagem');
      }

      // Limpa o estado após o post ser criado com sucesso
      setPostType("text"); 
      setPostText("");
      setMediaFile(null);
      setMediaPreview(null);
      setEventDate(new Date());
      setModalOpen(false);
    } catch (error) {
      console.error('Erro ao criar postagem:', error);
      alert('Erro ao criar postagem. Tente novamente.'); // Exibe um alerta de erro
    }
  };

  const renderModalContent = () => {
    switch (postType) {
      case "text":
        return (
          <div className="modal-content">
            <textarea
              placeholder="O que você está pensando?"
              value={postText}
              onChange={(e) => setPostText(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="flex justify-end">
              <button
                type="submit" 
                className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "photo":
        return (
          <div className="modal-content">
            <label
              htmlFor="mediaUpload"
              className="block text-white font-semibold mb-1 cursor-pointer"
            >
              Upload de Foto:
            </label>
            <input
              type="file"
              id="mediaUpload"
              accept="image/*"
              onChange={handleMediaChange}
              className="hidden"
            />
            {mediaPreview && (
              <img
                src={mediaPreview}
                alt="Preview"
                className="max-w-full h-48 rounded-lg"
              />
            )}
            <div className="flex justify-end mt-4">
              <button
                type="submit" 
                className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "video":
        return (
          <div className="modal-content">
            <label
              htmlFor="mediaUpload"
              className="block text-white font-semibold mb-1 cursor-pointer"
            >
              Upload de Vídeo:
            </label>
            <input
              type="file"
              id="mediaUpload"
              accept="video/*"
              onChange={handleMediaChange}
              className="hidden"
            />
            {mediaPreview && (
              <video
                src={mediaPreview}
                controls
                className="max-w-full h-48 rounded-lg"
              ></video>
            )}
            <div className="flex justify-end mt-4">
              <button
                type="submit" 
                className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "meet":
        return (
          <div className="modal-content">
            <input 
              type="text" 
              id="meetTitle" 
              placeholder="Título do Meet"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input 
              type="text" 
              id="meetLink" 
              placeholder="Link da Sala"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição"
              value={postText}
              onChange={(e) => setPostText(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="flex justify-end mt-4">
              <button
                type="submit" 
                className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "startup":
        return (
          <div className="modal-content">
            <input 
              type="text" 
              id="startupName" 
              placeholder="Nome da Startup"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição"
              value={postText}
              onChange={(e) => setPostText(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="flex justify-end mt-4">
              <button
                type="submit" 
                className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      case "evento":
        return (
          <div className="modal-content">
            <input 
              type="text" 
              id="eventName" 
              placeholder="Nome do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input 
              type="text" 
              id="eventLocation" 
              placeholder="Local do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div className="mb-2">
              <label
                className="block text-white font-semibold mb-1"
              >
                Data e Hora do Evento:
              </label>
              <DatePicker
                selected={eventDate}
                onChange={(date) => setEventDate(date)}
                showTimeSelect
                dateFormat="dd/MM/yyyy HH:mm"
                timeFormat="HH:mm"
                timeIntervals={15}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
              />
            </div>
            <label
              htmlFor="mediaUpload"
              className="block text-white font-semibold mb-1 cursor-pointer"
            >
              Upload de Foto para o Evento:
            </label>
            <input
              type="file"
              id="mediaUpload"
              accept="image/*"
              onChange={handleMediaChange}
              className="hidden"
            />
            {mediaPreview && (
              <img
                src={mediaPreview}
                alt="Preview"
                className="max-w-full h-48 rounded-lg"
              />
            )}
            <div className="flex justify-end mt-4">
              <button
                type="submit" 
                className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full"
              >
                Postar
              </button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-gray-900 border border-gray-700 rounded-md p-4 mb-4">
      <div className="flex items-center mb-4 bg-gray-900">
        <input
          type="text"
          placeholder="Crie uma publicação"
          value={postText}
          onChange={(e) => setPostText(e.target.value)}
          className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
        />
        <button
          onClick={() => setModalOpen(true)}
          className="ml-2 bg-blue-500 hover:bg-blue-600 text-white text-sm py-2 px-2 rounded-full"
        >
          Postar
        </button>
      </div>
      <div className="page-home-uploads-section flex justify-between text-white font-semibold">
        <button
          onClick={() => handlePostTypeChange("text")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-blue-500 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaLine className="text-white" />
          </span>
          Texto
        </button>
        <button
          onClick={() => handlePostTypeChange("photo")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-green-500 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaCamera className="text-white" />
          </span>
          Foto
        </button>
        <button
          onClick={() => handlePostTypeChange("video")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-red-500 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaVideo className="text-white" />
          </span>
          Vídeo
        </button>
        <button
          onClick={() => handlePostTypeChange("meet")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-500 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaVideo className="text-white" />
          </span>
          Meet
        </button>
        <button
          onClick={() => handlePostTypeChange("startup")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-500 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaLine className="text-white" />
          </span>
          StartUp
        </button>
        <button
          onClick={() => handlePostTypeChange("event")}
          className="flex items-center px-2 rounded-md hover:bg-gray-800"
        >
          <span
            className="bg-yellow-500 rounded-full p-2 mr-2"
            style={{ width: "24px", height: "24px" }}
          >
            <FaCalendar className="text-white" />
          </span>
          Evento
        </button>
      </div>
      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center">
          <div className="bg-gray-800 rounded-lg p-6 w-full max-w-lg">
            <div className="flex justify-end">
              <button
                onClick={() => setModalOpen(false)}
                className="text-white font-bold"
              >
                ×
              </button>
            </div>
            <form onSubmit={handlePostSubmit}> 
              {renderModalContent()}
            </form> 
          </div>
        </div>
      )}
    </div>
  );
}
