              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="create-post bg-gray-800 p-4 rounded-lg shadow-md">
      <div className="flex items-center">
        <img src={CarexLogo} alt="CarexLogo" className="w-12 h-12 rounded-full" />
        <button
          onClick={() => setModalOpen(true)}
          className="ml-4 w-full bg-gray-700 text-white py-2 px-4 rounded-full text-left focus:outline-none hover:bg-gray-600"
        >
          Criar uma publicação
        </button>
      </div>
      <div className="flex justify-around mt-4">
        <button
          onClick={() => handlePostTypeChange('meet')}
          className="flex items-center space-x-2 bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-full"
        >
          <FaLine className="text-white" />
          <span>Meet</span>
        </button>
        <button
          onClick={() => handlePostTypeChange('startup')}
          className="flex items-center space-x-2 bg-yellow-500 hover:bg-yellow-600 text-white py-2 px-4 rounded-full"
        >
          <FaCamera className="text-white" />
          <span>Startup</span>
        </button>
        <button
          onClick={() => handlePostTypeChange('evento')}
          className="flex items-center space-x-2 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-full"
