import React, { useState } from 'react';
import axios from 'axios';
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import CarexLogo from '../../assets/img/carex_logo.png'; // Certifique-se de que o caminho para o logo esteja correto

export function CreatePost() {
  const [postType, setPostType] = useState('');
  const [description, setDescription] = useState('');
  const [mediaFile, setMediaFile] = useState(null);
  const [mediaPreview, setMediaPreview] = useState('');
  const [eventDate, setEventDate] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [nomeEvento, setNomeEvento] = useState('');
  const [localEvento, setLocalEvento] = useState('');
  const [nomeStartup, setNomeStartup] = useState('');
  const [descricaoStartup, setDescricaoStartup] = useState('');

  const handlePostTypeChange = (type) => {
    setPostType(type);
    setModalOpen(true);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    setMediaFile(file);
    setMediaPreview(URL.createObjectURL(file));
  };

  const handlePost = async () => {
    const formData = new FormData();
    formData.append('postType', postType);
    formData.append('description', description);
    if (mediaFile) {
      formData.append('mediaFile', mediaFile);
    }
    if (eventDate) {
      formData.append('eventDate', eventDate.toISOString());
    }
    formData.append('nomeEvento', nomeEvento);
    formData.append('localEvento', localEvento);
    formData.append('nomeStartup', nomeStartup);
    formData.append('descricaoStartup', descricaoStartup);

    try {
      await axios.post('http://localhost:3000/posts', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      // Limpar campos após o envio
      setPostType('');
      setDescription('');
      setMediaFile(null);
      setMediaPreview('');
      setEventDate(null);
      setNomeEvento('');
      setLocalEvento('');
      setNomeStartup('');
      setDescricaoStartup('');
      setModalOpen(false);
    } catch (error) {
      console.error('Erro ao salvar o post:', error);
    }
  };

  const renderModalContent = () => {
    switch (postType) {
      case 'meet':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Meet</h2>
            <textarea
              placeholder="Escreva uma descrição..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'startup':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Startup</h2>
            <input
              type="text"
              placeholder="Nome da Startup"
              value={nomeStartup}
              onChange={(e) => setNomeStartup(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              value={descricaoStartup}
              onChange={(e) => setDescricaoStartup(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'evento':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post Evento</h2>
            <input
              type="text"
              placeholder="Nome do Evento"
              value={nomeEvento}
              onChange={(e) => setNomeEvento(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <DatePicker
              selected={eventDate}
              onChange={(date) => setEventDate(date)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Local do Evento"
              value={localEvento}
              onChange={(e) => setLocalEvento(e.target.value)}
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      case 'midias':
        return (
          <div className="modal-content">
            <h2 className="text-white font-bold text-xl mb-4">Criar Post de Mídias</h2>
            <textarea
              placeholder="Escreva uma descrição..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
              {mediaPreview && (
                <div className="mt-2">
                  {mediaFile.type.startsWith('image') ? (
                    <img src={mediaPreview} alt="Preview" className="max-w-full h-48 rounded-lg" />
                  ) : (
                    <video src={mediaPreview} controls className="max-w-full h-48 rounded-lg"></video>
                  )}
                </div>
              )}
            </div>
            <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-full mt-4">
              Postar
            </button>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-gray-900 p-6 rounded-lg shadow-md">
      <div className="flex items-center mb-4">
        <img src={CarexLogo} alt="Logo" className="w-10 h-10 rounded-full" />
        <input
          type="text"
          placeholder="Criar uma publicação..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="flex-1 ml-4 h-12 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
          onClick={() => setModalOpen(true)}
        />
      </div>
      <div className="flex justify-around">
        <button
          className="flex items-center text-blue-500"
          onClick={() => handlePostTypeChange('meet')}
        >
          <FaLine className="mr-2" /> Meet
        </button>
        <button
          className="flex items-center text-green-500"
          onClick={() => handlePostTypeChange('startup')}
        >
          <FaCamera className="mr-2" /> Startup
        </button>
        <button
          className="flex items-center text-red-500"
          onClick={() => handlePostTypeChange('evento')}
        >
          <FaCalendar className="mr-2" /> Evento
        </button>
        <button
          className="flex items-center text-purple-500"
          onClick={() => handlePostTypeChange('midias')}
        >
          <FaVideo className="mr-2" /> Mídias
        </button>
      </div>

      {modalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-gray-800 p-8 rounded-lg max-w-lg w-full">
            <button
              onClick={() => setModalOpen(false)}
              className="absolute top-4 right-4 text-white"
            >
              &times;
            </button>
            {renderModalContent()}
          </div>
        </div>
      )}
    </div>
  );
}
