import React, { useState } from 'react';
import { Dialog, Transition } from '@headlessui/react';

export function Register() {
  const [tipoUsuario, setTipoUsuario] = useState(null); 
  const [formData, setFormData] = useState({
    // Campos para Empresa
    NomeRepresentante: '',
    NomeEmpresa: '',
    NIF: '',
    AnosDeExistencia: '',
    // Campos para Empreendedor
    Nome: '',
    Genero: '',
    DataNascimento: '',
    BI: '',
    // Campos comuns
    Email: '',
    Endereco: '',
    Telefone: '',
    AreaAtuacao: '',
    Provincia: '',
    Senha: '',
  });

  const [showModal, setShowModal] = useState(false); 
  const [modalMessage, setModalMessage] = useState(''); 
  const [modalType, setModalType] = useState('success'); 

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ...formData, tipo: tipoUsuario }),
      });

      if (response.ok) {
        const data = await response.json();
        setModalMessage(`Usuário e ${tipoUsuario} criados com sucesso!`);
        setModalType('success');
        setShowModal(true);
      } else {
        const errorData = await response.json();
        setModalMessage(errorData.error);
        setModalType('error');
        setShowModal(true);
      }
    } catch (error) {
      console.error('Erro ao registrar:', error);
      setModalMessage('Ocorreu um erro ao registrar. Tente novamente mais tarde.');
      setModalType('error');
      setShowModal(true);
    }
  };

  const handleTipoChange = (e) => {
    setTipoUsuario(e.target.value);
  };

  return (
    <div className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl mx-auto">
      <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <h2 className="text-2xl font-bold mb-4">Registro de Usuário</h2>
        <div className="flex justify-center mb-4">
          <button
            onClick={() => setTipoUsuario('empresa')}
            className={`bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ${tipoUsuario === 'empresa' ? 'bg-blue-700' : ''}`}
          >
            Empresa
          </button>
          <button
            onClick={() => setTipoUsuario('empreendedor')}
            className={`bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ml-2 ${tipoUsuario === 'empreendedor' ? 'bg-blue-700' : ''}`}
          >
            Empreendedor
          </button>
        </div>
        {tipoUsuario && (
          <div>
            {/* Campos para Empresa */}
            {tipoUsuario === 'empresa' && (
              <div className="mb-4">
                <h3 className="text-xl font-bold mb-2">Registro de Empresa</h3>
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label htmlFor="NomeRepresentante" className="block text-gray-700 text-sm font-bold mb-2">
                      Nome do Representante:
                    </label>
                    <input
                      type="text"
                      id="NomeRepresentante"
                      name="NomeRepresentante"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.NomeRepresentante}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label htmlFor="NomeEmpresa" className="block text-gray-700 text-sm font-bold mb-2">
                      Nome da Empresa:
                    </label>
                    <input
                      type="text"
                      id="NomeEmpresa"
                      name="NomeEmpresa"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.NomeEmpresa}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label htmlFor="NIF" className="block text-gray-700 text-sm font-bold mb-2">
                      NIF:
                    </label>
                    <input
                      type="text"
                      id="NIF"
                      name="NIF"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.NIF}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label htmlFor="AnosDeExistencia" className="block text-gray-700 text-sm font-bold mb-2">
                      Anos de Existência:
                    </label>
                    <input
                      type="number"
                      id="AnosDeExistencia"
                      name="AnosDeExistencia"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.AnosDeExistencia}
                      onChange={handleChange}
                    />
                  </div>
                </div>
              </div>
            )}
            {/* Campos para Empreendedor */}
            {tipoUsuario === 'empreendedor' && (
              <div className="mb-4">
                <h3 className="text-xl font-bold mb-2">Registro de Empreendedor</h3>
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label htmlFor="Nome" className="block text-gray-700 text-sm font-bold mb-2">
                      Nome:
                    </label>
                    <input
                      type="text"
                      id="Nome"
                      name="Nome"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.Nome}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label htmlFor="Genero" className="block text-gray-700 text-sm font-bold mb-2">
                      Género:
                    </label>
                    <input
                      type="text"
                      id="Genero"
                      name="Genero"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.Genero}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label htmlFor="DataNascimento" className="block text-gray-700 text-sm font-bold mb-2">
                      Data de Nascimento:
                    </label>
                    <input
                      type="date"
                      id="DataNascimento"
                      name="DataNascimento"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.DataNascimento}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label htmlFor="BI" className="block text-gray-700 text-sm font-bold mb-2">
                      BI:
                    </label>
                    <input
                      type="text"
                      id="BI"
                      name="BI"
                      className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                      value={formData.BI}
                      onChange={handleChange}
                    />
                  </div>
                </div>
              </div>
            )}
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
              <div>
                <label htmlFor="Email" className="block text-gray-700 text-sm font-bold mb-2">
                  Email:
                </label>
                <input
                  type="email"
                  id="Email"
                  name="Email"
                  className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.Email}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label htmlFor="Endereco" className="block text-gray-700 text-sm font-bold mb-2">
                  Endereço:
                </label>
                <input
                  type="text"
                  id="Endereco"
                  name="Endereco"
                  className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.Endereco}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label htmlFor="Telefone" className="block text-gray-700 text-sm font-bold mb-2">
                  Telefone:
                </label>
                <input
                  type="text"
                  id="Telefone"
                  name="Telefone"
                  className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.Telefone}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label htmlFor="AreaAtuacao" className="block text-gray-700 text-sm font-bold mb-2">
                  Área de Atuação:
                </label>
                <input
                  type="text"
                  id="AreaAtuacao"
                  name="AreaAtuacao"
                  className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.AreaAtuacao}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label htmlFor="Provincia" className="block text-gray-700 text-sm font-bold mb-2">
                  Província:
                </label>
                <input
                  type="text"
                  id="Provincia"
                  name="Provincia"
                  className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.Provincia}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label htmlFor="Senha" className="block text-gray-700 text-sm font-bold mb-2">
                  Senha:
                </label>
                <input
                  type="password"
                  id="Senha"
                  name="Senha"
                  className="capitalize w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.Senha}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="flex items-center justify-between mt-4">
              <button
                type="submit"
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Registrar
              </button>
              <button
                type="submit"
                className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={() => {
                  // Redirecionar para a página inicial
                  window.location.href = '/'; 
                }}
              >
                Cancelar
              </button>
            </div>
          </div>
        )}
      </div>
      {/* Modal */}
      {showModal && (
        <div
          className={`fixed z-10 inset-0 overflow-y-auto ${
            modalType === 'success'
              ? 'bg-green-500 bg-opacity-50'
              : 'bg-red-500 bg-opacity-50'
          }`}
        >
          <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
            <div
              className={`fixed inset-0 transition-opacity bg-gray-900 bg-opacity-50 z-10 ${
                modalType === 'success'
                  ? 'bg-green-500 bg-opacity-50'
                  : 'bg-red-500 bg-opacity-50'
              }`}
            />
            <span
              className="hidden sm:inline-block sm:align-middle sm:h-screen sm:w-screen"
            />
            <div
              className={`inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full ${
                modalType === 'success' ? 'sm:p-6' : 'sm:p-4'
              }`}
            >
              <div className="bg-gray-100 px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="sm:flex sm:items-start">
                  <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full">
                    {modalType === 'success' ? (
                      <svg
                        className="h-6 w-6 text-green-400"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M9 12l2 2 4-4m6 6V3M12 21h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                        />
                      </svg>
                    ) : (
                      <svg
                        className="h-6 w-6 text-red-400"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.869-3.228l-1.259-2.772A2.25 2.25 0 0018 9.75v-2.25c0-1.22-.875-2.25-2.031-2.25H8.062a2.25 2.25 0 00-2.031 2.25v2.25c0 1.22.875 2.25 2.031 2.25H12z"
                        />
                      </svg>
                    )}
                  </div>
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                    <h3
                      className={`text-lg leading-6 font-medium text-gray-900 ${
                        modalType === 'success'
                          ? 'text-green-900'
                          : 'text-red-900'
                      }`}
                    >
                      {modalMessage}
                    </h3>
                  </div>
                </div>
              </div>
              <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:py-4">
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className="mt-3 inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                >
                  Fechar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
